mapper.select:
SELECT user_id
FROM user
WHERE user_id= #{userId}

SELECT pet_id
FROM pet
WHERE pet_id= #{petId}

SELECT calendar_id
FROM calendar
WHERE calendar_id= #{calendarId}

SELECT list_id
FROM todolist
WHERE list_id= #{listId}

SELECT is_completed
FROM todolist
WHERE list_id= #{listId}

SELECT nickname
FROM user
WHERE nickname= #{nickname}
