mapper.select:
SELECT is_completed AS isCompleted
FROM todolist
WHERE list_id = #{listId}

mapper.update:
UPDATE  todolist
SET  is_completed = 1
WHERE list_id = #{listId}

UPDATE  todolist
SET  is_completed = 0
WHERE list_id = #{listId}

mapper.delete:
DELETE FROM todolist
WHERE user_id = #{userId}
AND is_completed = 1
